import React, { useState, useEffect } from 'react';

const Experiment = ({ history }) => {
  const [timer, setTimer] = useState(5); // Initial timer value in seconds

  // Countdown logic
  useEffect(() => {
    const countdown = setInterval(() => {
      setTimer((prevTimer) => (prevTimer > 0 ? prevTimer - 1 : 0));
    }, 1000);

    return () => clearInterval(countdown); // Clean up interval on component unmount
  }, []);

  // Handler for start button click
  const handleStartButtonClick = () => {
    // Play audio file logic can be added here
    // For simplicity, let's simulate audio playback with a setTimeout
    setTimeout(() => {
      // Redirect to slider bars page after audio finishes
      history.push('/slider-bars');
    }, 5000); // Simulate 5 seconds audio duration
  };

  return (
    <div>
      <p style={{ fontSize: '45px' }}>Timer: {timer}</p>
      <div>
        {/* Start button */}
        <button className="App-button" onClick={handleStartButtonClick} disabled={timer !== 0}>
          Start
        </button>
      </div>
    </div>
  );
};

export default Experiment;
