import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './App.css';
import './ConsentPage.css';

const ConsentPage = () => {
  const [isChecked, setIsChecked] = useState(false);
  const navigate = useNavigate();

  const handleCheckboxChange = (e) => {
    setIsChecked(e.target.checked);
  };

  const handleNextButtonClick = () => {
    if (isChecked) {
      // Navigate to the next page if checkbox is checked
      navigate('/demographic');
    } else {
      // Display an alert or handle the case when checkbox is not checked
      alert('Please agree to the terms and conditions to proceed.');
    }
  };

  return (
    <div className="consent-container">
      <h1>Terms and Conditions</h1>
      <div className="terms-container">
        <div className="scrollable-box">
          {/* Scrollable box for terms and conditions */}
          <p>
            Tick the box to continue 
          </p>
          {/* Add more terms and conditions as needed */}
        </div>
        <div className="checkbox-container">
          <input type="checkbox" id="checkbox" checked={isChecked} onChange={handleCheckboxChange} />
          <label htmlFor="checkbox">I agree to the terms and conditions</label>
        </div>
      </div>
      <button className="next-button" onClick={handleNextButtonClick} disabled={!isChecked}>
        Next
      </button>
    </div>
  );
};

export default ConsentPage;
