import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom'; // Importing hook for navigation
import useSound from 'use-sound'; // Importing hook for playing audio
import './App.css'; // Importing CSS file

const Experiment = () => {
  const [timer, setTimer] = useState(3); // State variable to store timer value, initialized to 3 seconds
  const navigate = useNavigate(); // Hook for navigation
  const [playSound, { stop }] = useSound(); // Hook for playing audio, with a reference to stop the sound

  const [clickCount] = useState(() => {
    // Retrieve click count from local storage or default to 0
    const storedClickCount = localStorage.getItem('clickCount');
    return storedClickCount ? parseInt(storedClickCount, 10) : 1;
  });

  // List of audio files
  const audioFiles = [
    require('./audios/193.mp3'),
    require('./audios/367.mp3'),
    require('./audios/714.mp3'),
    // Add more audio files as needed
  ];

  // Countdown logic
  useEffect(() => {
    const countdown = setInterval(() => {
      // Decrease timer by 1 second every second, but not below 0
      setTimer((prevTimer) => (prevTimer > 0 ? prevTimer - 1 : 0));
    }, 1000);

    return () => clearInterval(countdown);
  }, []);

  // Handler for start button click
  const handleStartButtonClick = () => {
    // Play audio file based on click count
    playSound({ sound: audioFiles[clickCount - 1] });

    // Stop audio after 10 seconds and navigate to '/sliders'
    setTimeout(() => {
      stop(); // Stop the audio
      navigate('/sliders'); // Navigate to '/sliders' route
    }, 10000); // 10 seconds delay
  };

  return (
    <div>
      {/* Display timer value */}
      <p style={{ fontSize: '45px' }}>{timer}</p>
      <div>
        {/* Start button */}
        <button className="App-button" onClick={handleStartButtonClick} disabled={timer !== 0}>
          Start
        </button>
      </div>
    </div>
  );
};

export default Experiment;
