// Instructions.js
import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './App.css';

// ConsentPage component
const Instructions = () => {

  return (
    <div>
      {/* Terms and Conditions */}
      <p style={{ fontSize: '45px' }}>Instructions</p>
      <div style={{ height: '400px', overflowY: 'auto', border: '1px solid white', borderRadius: '5px', padding: '10px', width: '850px' }}>
        {/* Scrollable box for terms and conditions */}
        <p style={{ fontSize: '20px' }}>
          Please read these instructions carefully to complete the experiment smoothly:
        </p>
        {/* Individual terms */}
        <p style={{ fontSize: '14px' }}>
          1. When you click the "Next" button below, a timer will appear. When you start the timer, the experiment begins. When the timer hits zero, an audio file will play. Please listen to the audio attentively.
        </p>
        <p style={{ fontSize: '14px' }}>
          2. After the audio is finished playing, you will be asked to report how the audio changes your mood
        </p>
        <p style={{ fontSize: '14px' }}>
          3. Valence of an emotion refers to extent to which an emotion is positive or negative, and arousal refers to the intensity of the emotion.
        </p>
        <p style={{ fontSize: '14px' }}>
          4. After reporting your emotion, the experiment will repeat. That is you will see the timer again, and when you start the timer, the same process will repeat.
        </p>
        <p style={{ fontSize: '14px' }}>
          5. You will be allowed between 5 to 15 repititions of the experiment. You can quit anytime after 5 repititions, by clicking the "Quit" button.
        </p>
        {/* Add more terms and conditions as needed */}
      </div>
      {/* Checkbox */}
      <div>
        <input type="checkbox" checked={isChecked} onChange={handleCheckboxChange} />
        <label htmlFor="checkbox" style={{ fontSize: '20px' }}>I agree to the terms and conditions</label>
      </div>
      {/* Next button */}
      <button className="App-button" onClick={handleNextButtonClick} disabled={!isChecked}>
        Next
      </button>
    </div>
  );
};


export default Instructions;
