import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const DemographicPage = () => {
  const [isChecked, setIsChecked] = useState(false);
  const navigate = useNavigate();

  const handleCheckboxChange = (e) => {
    setIsChecked(e.target.checked);
  };

  const handleNextButtonClick = () => {
    if (isChecked) {
      // Navigate to the next page if checkbox is checked
      navigate('/demographic');
    } else {
      // Display an alert or handle the case when checkbox is not checked
      alert('Please agree to the terms and conditions to proceed.');
    }
  };

  return (
    <div>
      <h1>Consent Page</h1>
      <div style={{ height: '400px', overflowY: 'auto', border: '1px solid white', borderRadius: '5px', padding: '10px', width: '850px' }}>
        {/* Scrollable box for questionnaire */}
        <p style={{ fontSize: '20px' }}>Please answer the following questions:</p>
        <p style={{ fontSize: '16px' }}>1. Are you above the age of 18?</p>
        {/* Add more questions as needed */}
      </div>
      <div>
        <input type="checkbox" checked={isChecked} onChange={handleCheckboxChange} />
        <label htmlFor="checkbox" style={{ fontSize: '20px' }}>I agree to participate in the study</label>
      </div>

      <button className="App-button" onClick={handleNextButtonClick} disabled={!isChecked}>
        Next
      </button>
    </div>
  );
};

export default DemographicPage;
