import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './App.css';

const SliderPage = () => {
  const navigate = useNavigate();
  const [valence, setValence] = useState(5); // Initial value for valence slider
  const [arousal, setArousal] = useState(5); // Initial value for arousal slider
  const [clickCount, setClickCount] = useState(0); // Counter for button clicks

  // Handler for slider changes
  const handleValenceChange = (e) => {
    setValence(Number(e.target.value));
  };

  const handleArousalChange = (e) => {
    setArousal(Number(e.target.value));
  };

  // Handler for next button click
  const handleNextButtonClick = () => {
    // Increment click count
    setClickCount((prevCount) => prevCount + 1);

    if (clickCount >= 3) {
      // Redirect to thank you page if click count exceeds 10
      navigate('/thankyou');
    } else {
      // Redirect back to the timer page to restart the process
      navigate('/experiment');
    }
  };

  return (
    <div>
      <p style={{ fontSize: '25px' }}>Arousal is an emotional dimension of musically energy level, while Valence is an emotional dimension of the comfortable level of the listener </p>
      <p style={{ fontSize: '35px' }}>Please report the extent of what you feel about the valence and arousal of the audio played previously </p>
      <div style={{ marginBottom: '20px' }}>
        {/* Valence slider */}
        <label htmlFor="valenceSlider">Valence:</label>
        <input
          type="range"
          id="valenceSlider"
          name="valenceSlider"
          min="1"
          max="10"
          value={valence}
          onChange={handleValenceChange}
        />
        <span>{valence}</span>
      </div>
      <div style={{ marginBottom: '20px' }}>
        {/* Arousal slider */}
        <label htmlFor="arousalSlider">Arousal:</label>
        <input
          type="range"
          id="arousalSlider"
          name="arousalSlider"
          min="1"
          max="10"
          value={arousal}
          onChange={handleArousalChange}
        />
        <span>{arousal}</span>
      </div>
      {/* Next button */}
      <button className="App-button" onClick={handleNextButtonClick}>
        Next
      </button>
    </div>
  );
};

export default SliderPage;
