import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import useSound from 'use-sound';
import mySound from './audios/89.mp3';
import './App.css';

const Experiment = () => {
  const [timer, setTimer] = useState(3); // Initial timer value in seconds
  const navigate = useNavigate();
  // const audio = new Audio("./audios/89.mp3");
  const [playSound] = useSound(mySound);


  // Countdown logic
  useEffect(() => {
    const countdown = setInterval(() => {
      setTimer((prevTimer) => (prevTimer > 0 ? prevTimer - 1 : 0));
    }, 1000);

    return () => clearInterval(countdown); // Clean up interval on component unmount
  }, []);

  // Handler for start button click
  const handleStartButtonClick = () => {
    // Play audio file
    // audio.play();
    playSound();

    // Redirect to sliders page after audio finishes
    setTimeout(() => {
      navigate('/sliders');
    }, 5000); // Audio duration is 5 seconds
  };

  return (
    <div>
      <p style={{ fontSize: '45px' }}>{timer}</p>
      <div>
        {/* Start button */}
        <button className="App-button" onClick={handleStartButtonClick} disabled={timer !== 0}>
          Start
        </button>
      </div>
    </div>
  );
};

export default Experiment;
