import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './App.css';

const ConsentPage = () => {
  const [isChecked, setIsChecked] = useState(false);
  const navigate = useNavigate();

  const handleCheckboxChange = (e) => {
    setIsChecked(e.target.checked);
  };

  const handleNextButtonClick = () => {
    if (isChecked) {
      // Navigate to the next page if checkbox is checked
      navigate('/next-page');
    } else {
      // Display an alert or handle the case when checkbox is not checked
      alert('Please agree to the terms and conditions to proceed.');
    }
  };

  return (
    <div>
      <h1>Terms and Conditions</h1>
      <div style={{ height: '200px', overflowY: 'auto', border: '1px solid #ccc', padding: '10px' }}>
        {/* Scrollable box for terms and conditions */}
        <p>
          Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nulla nec eros vitae mauris
          eleifend euismod. Integer eget metus in erat congue consequat. Ut mollis sapien non
          metus fermentum, auctor aliquam mi sollicitudin. Duis convallis venenatis leo nec
          sollicitudin. Nulla ut quam nisl. Donec auctor, dui vitae convallis efficitur, sapien
          nisi rutrum urna, et ultrices lorem odio vitae ipsum. In hac habitasse platea dictumst.
          Suspendisse faucibus, lectus eget viverra molestie, metus magna consequat lorem, id
          volutpat ex sapien eget ex. Aliquam ornare ut purus ut posuere. Curabitur eu mi
          scelerisque, egestas velit at, euismod justo. Duis feugiat neque sed lectus efficitur,
          nec vestibulum purus pellentesque. Integer in ligula eget turpis faucibus euismod ac non
          felis.
        </p>
        {/* Add more terms and conditions as needed */}
      </div>
      <div>
        <input type="checkbox" checked={isChecked} onChange={handleCheckboxChange} />
        <label htmlFor="checkbox">I agree to the terms and conditions</label>
      </div>
      <button onClick={handleNextButtonClick} disabled={!isChecked}>
        Next
      </button>
    </div>
  );
};

export default ConsentPage;
